cypher:
CREATE (factory:Factory {id: 'example-factory-id', Factory: 'example-factory', Location: 'example-location'});
CREATE (machine:Machine {id: 'example-machine-id', Machine Type: 'example-machine type', Machine Utilization (%): 'example-machine utilization (%)', Machine Downtime (hours): 'example-machine downtime (hours)'});
CREATE (batch:Batch {id: 'example-batch-id', Batch: 'example-batch', Batch Quality (Pass %): 'example-batch quality (pass %)', Cycle Time (minutes): 'example-cycle time (minutes)'});
CREATE (shift:Shift {id: 'example-shift-id', Shift: 'example-shift', Operator Experience (years): 'example-operator experience (years)', Team Size: 'example-team size'});
CREATE (product:Product {id: 'example-product-id', Product Category: 'example-product category', Supplier: 'example-supplier', Supplier Delays (days): 'example-supplier delays (days)'});
MATCH (factory:Factory {id: 'example-factory-id'}), (machine:Machine {id: 'example-machine-id'}) CREATE (factory)-[r:HAS_MACHINE]->(machine);
MATCH (machine:Machine {id: 'example-machine-id'}), (batch:Batch {id: 'example-batch-id'}) CREATE (machine)-[r:PRODUCES_BATCH]->(batch);
MATCH (batch:Batch {id: 'example-batch-id'}), (shift:Shift {id: 'example-shift-id'}) CREATE (batch)-[r:OPERATES_IN_SHIFT]->(shift);
MATCH (shift:Shift {id: 'example-shift-id'}), (product:Product {id: 'example-product-id'}) CREATE (shift)-[r:PRODUCES_PRODUCT]->(product);
