cypher:
CREATE (factory:Factory {id: 'example-factory-id', Factory: 'example-factory', Location: 'example-location'});
CREATE (machine:Machine {id: 'example-machine-id', Machine Type: 'example-machine type', Machine Utilization (%): 'example-machine utilization (%)', Machine Downtime (hours): 'example-machine downtime (hours)'});
CREATE (batch:Batch {id: 'example-batch-id', Batch: 'example-batch', Batch Quality (Pass %): 'example-batch quality (pass %)', Cycle Time (minutes): 'example-cycle time (minutes)'});
CREATE (shift:Shift {id: 'example-shift-id', Shift: 'example-shift', Operator Experience (years): 'example-operator experience (years)', Team Size: 'example-team size'});
CREATE (supplier:Supplier {id: 'example-supplier-id', Supplier: 'example-supplier', Supplier Delays (days): 'example-supplier delays (days)', Raw Material Quality: 'example-raw material quality'});
CREATE (product:Product {id: 'example-product-id', Product Category: 'example-product category', Market Demand Index: 'example-market demand index', Cost of Downtime ($): 'example-cost of downtime ($)'});
MATCH (batch:Batch {id: 'example-batch-id'}), (factory:Factory {id: 'example-factory-id'}) CREATE (batch)-[r:PRODUCED_IN]->(factory);
MATCH (batch:Batch {id: 'example-batch-id'}), (machine:Machine {id: 'example-machine-id'}) CREATE (batch)-[r:USES]->(machine);
MATCH (shift:Shift {id: 'example-shift-id'}), (batch:Batch {id: 'example-batch-id'}) CREATE (shift)-[r:WORKED_ON]->(batch);
MATCH (product:Product {id: 'example-product-id'}), (supplier:Supplier {id: 'example-supplier-id'}) CREATE (product)-[r:SUPPLIED_BY]->(supplier);
MATCH (batch:Batch {id: 'example-batch-id'}), (product:Product {id: 'example-product-id'}) CREATE (batch)-[r:PRODUCES]->(product);
